body {
	background: black;
	padding: 0;
	margin: 0;
	color: white;
	transition: transform 1s;
}

.circle {
	position: fixed;
	left: 0;
	top: 0;
	width: 400px;
	height: 400px;
	border-radius: 400px;
	background: red;
	transition: transform .2s;
	opacity:.9;
	background-blend-mode: multiply;
	transform-origin: center center;
}

#circle-1 {
	background: white;
}

#circle-2 {
	background: magenta;
}

#circle-3 {
	background: cyan;
}

#circle-4 {
	background: yellow;
}

