class Passthru extends N {
  static get inputs() {
    return [{
      name: "in",
      observe: true,
      restrict: Number
    }];
  }

  static get outputs() {
    return ["out"];
  }

  onAttrChanged(name, oldValue, newValue) {
    switch (name) {
      case "in":
        this.send("out", newValue);
        break;
    }
  }
}

