class Circle extends N {
    static get type() { return N.SCREEN }

	static get inputs() {
	  return [
			{
				name: 'selector',
				observe: true,
				defaultValue: '',
				restrict: String,
			},
			{
			    name: 'x',
			    observe: true,
			    defaultValue: 0,
			    restrict: Number
			},
			{
			    name: 'y',
			    observe: true,
			    defaultValue: 0,
			    restrict: Number
			},
			{
			    name: 'scale',
			    observe: true,
			    defaultValue: 1,
			    restrict: N.float(0.01)
			}
		]
	}

	getEl() {
	    try {
	        let el = this.screen.querySelector(this.getAttribute('selector'))
	        if(el) return el;
	    } catch(e) {
	        return null;
	    }
	}

	// oldValue and newValue are always strings as these are
	// attributes on the component. Use this.getAttribute
	// to get the typed value
	onAttrChanged(name, oldValue, newValue) {
	    let el = this.getEl();
        if(!el) return;

        let x = this.getAttribute('x');
        let y = this.getAttribute('y');
        let scale = this.getAttribute('scale');

        el.style.transform = `translate(${x}vw, ${y}vh) scale(${scale})`
	}
}

