class X extends N {
	static get inputs() {
	  return [
			{
				name: 'x',
				observe: true,
				defaultValue: 'x',
				restrict: String
			}
		]
	}

	onAttrChanged(name, oldValue, newValue) {
	  this.screen.getElementById('x').innerText = newValue
	}
}

